import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import Header from "./Header"; // Assuming the Header component is in the same folder

const OtpVerification = () => {
  const [emailOtp, setEmailOtp] = useState("");
  const [mobileOtp, setMobileOtp] = useState("");
  const [emailVerified, setEmailVerified] = useState(false);
  const [mobileVerified, setMobileVerified] = useState(false);
  const navigate = useNavigate();

  const handleEmailVerify = async () => {
    try {
      const response = await axios.post("/api/verify-email", { otp: emailOtp });
      if (response.data.success) {
        setEmailVerified(true);
        alert("Email verified successfully!");
      } else {
        alert("Invalid OTP. Please try again.");
      }
    } catch (error) {
      console.error("Error verifying email OTP:", error);
    }
  };

  const handleMobileVerify = async () => {
    try {
      const response = await axios.post("/api/verify-mobile", {
        otp: mobileOtp,
      });
      if (response.data.success) {
        setMobileVerified(true);
        alert("Mobile verified successfully!");
        // Call login function if both OTPs are verified
        await loginUser();
      } else {
        alert("Invalid OTP. Please try again.");
      }
    } catch (error) {
      console.error("Error verifying mobile OTP:", error);
    }
  };

  const loginUser = async () => {
    try {
      const response = await axios.post("/api/login", {
        email: "user@example.com",
      });
      localStorage.setItem("token", response.data.token); // Store JWT in localStorage
      alert("Login successful!");
      navigate("/dashboard"); // Redirect to the dashboard
    } catch (error) {
      console.error("Login failed:", error);
    }
  };

  return (
    <div>
      <Header />

      <div style={styles.pageContainer}>
        <div style={styles.textSection}>
          <p>
            Lorem Ipsum is simply dummy text of the printing and typesetting
            industry. Lorem Ipsum has been the industry's standard dummy text
            ever since the 1500s, when an unknown printer took a galley.
          </p>
        </div>

        <div style={styles.formContainer}>
          <form style={styles.form}>
            <h2>OTP Verification</h2>

            <input
              type="text"
              placeholder="Email OTP"
              value={emailOtp}
              onChange={(e) => setEmailOtp(e.target.value)}
              disabled={emailVerified}
              style={styles.input}
            />
            <button
              onClick={handleEmailVerify}
              disabled={emailVerified}
              style={styles.button}
            >
              {emailVerified ? "Verified" : "Verify"}
            </button>

            <input
              type="text"
              placeholder="Mobile OTP"
              value={mobileOtp}
              onChange={(e) => setMobileOtp(e.target.value)}
              disabled={mobileVerified}
              style={styles.input}
            />
            <button
              onClick={handleMobileVerify}
              disabled={mobileVerified}
              style={styles.button}
            >
              {mobileVerified ? "Verified" : "Verify"}
            </button>
          </form>
        </div>
      </div>
    </div>
  );
};

const styles = {
  pageContainer: {
    // justifyContent: "center",
    // alignItems: "center",
    // padding: "20px",
    display: "flex",
    flexDirection: "row",
    height: "calc(100vh - 80px)",
    backgroundColor: "#ffff",
  },
  textSection: {
    flex: 1,
    padding: "20px",
    fontSize: "16px",
    lineHeight: "1.5",
    color: "#333",
  },
  formContainer: {
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    backgroundColor: "#fff",
    padding: "20px",
    border: "1px solid #ccc",
    borderRadius: "8px",
    width: "400px",
  },
  form: {
    width: "100%",
    display: "flex",
    flexDirection: "column",
    gap: "10px",
    padding: "20px",
    borderRadius: "8px",
    boxShadow: "0 0 10px rgba(0, 0, 0, 0.1)",
  },
  input: {
    padding: "10px",
    borderRadius: "4px",
    border: "1px solid #ccc",
    fontSize: "14px",
  },
  button: {
    padding: "10px",
    backgroundColor: "#007bff",
    color: "#fff",
    border: "none",
    borderRadius: "4px",
    cursor: "pointer",
    fontSize: "14px",
  },
};

export default OtpVerification;
